// ProductBox.js
import React, { useState, useEffect } from "react";
// import { API_URL } from "../../public/util/config";
const ProductBox = () => {
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const baseUrl = process.env.REACT_APP_BASE_URL || ''
    const fetchData = async () => {
      try {
        const response = await fetch(`${process.env.NEXT_PUBLIC_REST_ENDPOINT}/posts/1/comments`);
        if (!response.ok) {
          throw new Error("Failed to fetch product");
        }

        const productData = await response.json();
        console.log(productData);
        setProduct(productData);
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className='pt-10'>
      <h2 className='text-2xl border-b border-gray-100 pb-6 mb-6'>
        Product Box
      </h2>
      {product && (
        <div className='flex justify-between'>
          {product.map((item) => (
            <div className='flex flex-col w-44'>
              <h3 className='mb-6 font-bold text-lg line-clamp-1'>
                {item.name}
              </h3>
              <span className='font-medium text-sm line-clamp-2'>
                {item.body}
              </span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductBox;
